mapper.insert:

insert into user
set email = #{email}
    , upw = #{upw}
    , nickName = #{nickName}
    , agree = #{agree}

mapper.select:

select email
from user
where email = #{email}


select email, upw, userId, nickName
from user
where email = #{email}


select email, nickName,
from user
where userId = #{userId}

update user
set nickName = #{nickName}
where userId = #{userId}
